import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

// --- Customer Components ---
import Layout from './components/Layout';
import HomePage from './pages/HomePage';
import ProductDetailPage from './pages/ProductDetailPage';
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';
import CartPage from './pages/CartPage';
import CheckoutPage from './pages/CheckoutPage';
import OrderHistoryPage from './pages/OrderHistoryPage';
import { CartProvider } from './context/CartContext';

// --- Admin Components ---
import AdminLoginPage from './pages/AdminLoginPage'; // ✅ default export
import { AdminLayout } from './admin/AdminLayout'; // ✅ named export
import { AdminProtectedRoute } from './admin/AdminProtectedRoute'; // ✅ named export
import { AdminDashboardPage } from './admin/pages/AdminDashboardPage'; // ✅ named export
import { AdminProductListPage } from './admin/pages/AdminProductListPage'; // ✅ named export
import { AdminProductEditPage } from './admin/pages/AdminProductEditPage'; // ✅ named export

function App() {
  console.log({
    AdminLoginPage,
    AdminLayout,
    AdminProtectedRoute,
    AdminDashboardPage,
    AdminProductListPage,
    AdminProductEditPage,
  });

  return (
    <CartProvider>
      <Router>
        <Routes>

          {/* ------------------ CUSTOMER ROUTES ------------------ */}
          <Route path="/" element={<Layout />}>
            <Route index element={<HomePage />} />
            <Route path="product/:id" element={<ProductDetailPage />} />
            <Route path="login" element={<LoginPage />} />
            <Route path="register" element={<RegisterPage />} />
            <Route path="cart" element={<CartPage />} />
            <Route path="checkout" element={<CheckoutPage />} />
            <Route path="my-orders" element={<OrderHistoryPage />} />
          </Route>

          {/* ------------------ ADMIN ROUTES ------------------ */}
          {/* Admin Login (public) */}
          <Route path="/admin/login" element={<AdminLoginPage />} />

          {/* Protected admin section */}
          <Route element={<AdminProtectedRoute />}>
            <Route element={<AdminLayout />}>
              <Route path="/admin/dashboard" element={<AdminDashboardPage />} />
              <Route path="/admin/products" element={<AdminProductListPage />} />
              <Route path="/admin/products/new" element={<AdminProductEditPage />} />
              <Route path="/admin/products/edit/:id" element={<AdminProductEditPage />} />
            </Route>
          </Route>

        </Routes>
      </Router>
    </CartProvider>
  );
}

export default App;
